import React from 'react';
import ReactDOM from 'react-dom';
import SortableTree from './tree';
import { ReactView, UIFormController, UIView, HStack } from '@tuval/forms'
import FileExplorerTheme from 'react-sortable-tree-theme-file-explorer';

const manifest = {
    application: {
        name: 'com.celmino.widget.sortable-tree',
        path: './src/Application.tsx',
        displayName: "Procetra",
        icon: "data:image/svg+xml;base64,"

    }
    /* threads: {
        'PMThreadWorker': 'ThreadWorker.ts'
    } */
}


declare var tuval$core;

function App(manifest: any) {
    return <T extends { new(...args: any[]): {} }>(constructor: T) => {
        if (tuval$core['__APPS__'] == null) {
            tuval$core['__APPS__'] = {};
        }
        tuval$core['__APPS__'][manifest.application.name] = constructor;
    }
}



export class Tree extends React.Component<any, any> {
    constructor(props) {
        super(props);

        this.state = {
            treeData: [
                {
                    title: 'Chicken', children: ({ done }) => {
                        done([{
                            title: 'Egg', children: ({ done }) => {
                                done([{ title: 'son' }])
                            }
                        }]);
                    }
                },
                { title: 'Chicken1', children: [{ title: 'Egg1' }] },
                { title: 'Chicken2', children: [{ title: 'Egg2' }] }
            ],
        };
    }

    render() {
        return (

            <SortableTree

                scaffoldBlockPxWidth={32}
                rowHeight={() => 32}
                treeData={this.props.items}
                onChange={this.props.onChange}
                onMoveNode={this.props.onMoveNode}
                onVisibilityToggle={this.props.onVisibilityToggle}
                canDrag={this.props.canDrag}
            />

        );
    }
}


export class MyTestController extends UIFormController {

    public override LoadView(): UIView {
        const { treeItems, onChange, onMoveNode, onVisibilityToggle, canDrag } = this.props.config;
        return (
            HStack({ alignment: 'cTopLeading' })(
                ReactView(
                    <div style={{ height: '500px', width: '100%' }}>
                        <Tree items={treeItems} onChange={onChange} onMoveNode={onMoveNode} onVisibilityToggle={onVisibilityToggle} canDrag={canDrag} />
                    </div>
                )
            )

        )
    }
}
@App(manifest)
export class RatingWidget {
    public GetMainController() {
        return MyTestController
    }
}





